import { Component } from '@angular/core';
import { Producto } from './models/producto.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  
  // A5 dandole una serie de arrays de tipo Producto
 arrOficina:Producto[];
 arrAcceso:Producto[];

 //A16 creamos un array productoSeleccionados de tipo Producto[]
 //evidentemente hay que inicializar esrte array en constructor
 productosSeleccionados:Producto[];
 constructor(){

  // A5 inicializando el array de comida y lo que le voy a meter dentro son
// objetos de tipo producto...por lo tanto new objeto de tipo producto
// luego le paso los parametros ....El componente padre es el que conoce
// los arrays que luego le pasaremos a los hijos -> comphtml
   this.arrOficina=[
     new Producto('Escritorio', 'https://definicion.de/wp-content/uploads/2017/08/escritorio.jpg',1),
     new Producto('Impresora','data:image/jpeg;base64,',2),
    new Producto('Telefono','https://www.ecured.cu/images/2/2e/Telefonobrondi.jpg',1),
    new Producto('Silla','data:image/jpeg;base64,',7)
   ]
   this.arrAcceso=[
     new Producto('Tijera','data:image/jpeg;base64,',5),
     new Producto('Perforadora','data:image/jpeg;base64,',3),
     new Producto('Carpeta','data:image/jpeg;base64,',6),
     new Producto('Engrapadora','data:image/webp;base64,',2)
   ]

   //A16 inicializando  para posteriormente abjo donde estoy caputurando el evento
   //decirle que me lo agregue dentro
   this.productosSeleccionados=[];
 }
 //A 15 todo lo que fuese a enviar al hacer clik se vana a un array para
 //despues pasar a la comanda....entonces creamos un array en A16
 onProductoSeleccionado($event){
   //A17 decirle que me lo agregue dentro  nos vamos al ts de comanda porque si  
   //quiero recibir este array  por parametro lo tengo que pasar con un input A18
   //this.productosSeleccionados.push($event);  
   //B2  almacenaremos en una variable llamado productoEncontrado
   //si es que lo encontramos quiere decir que ya esta en un array por lo tanto se incrementa
   //Si no lo vamos a incluir dentro del arrar con push...ahora en comanda.html incluimos B3
   const productoEncontrado =this.productosSeleccionados.find(Producto=>Producto.nombre==$event.nombre)
   if(productoEncontrado){
     productoEncontrado.cant++;
   }else{
     $event.cant=1;
    this.productosSeleccionados.push($event);
   }
   
 }


}
